import React, { useState, useEffect } from "react";

const Stores = () => {
  const [stores, setStores] = useState([]);
  const [form, setForm] = useState({ name: "", type: "Franchise" });

  useEffect(() => {
    fetch("http://localhost:4000/api/stores")
      .then(res => res.json())
      .then(data => setStores(data));
  }, []);

  const handleSubmit = async () => {
    await fetch("http://localhost:4000/api/stores", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form)
    });
    setStores([...stores, form]);
    setForm({ name: "", type: "Franchise" });
  };

  const downloadStoresExcel = () => {
  window.open("http://localhost:4000/api/export/stores", "_blank");
};


<button onClick={downloadStoresExcel}>
  ðŸ“¥ Download Stores Excel
</button>


  return (
    <div>
      <h2>Stores</h2>
      <input
        placeholder="Store Name"
        value={form.name}
        onChange={e => setForm({ ...form, name: e.target.value })}
      />
      <select
        value={form.type}
        onChange={e => setForm({ ...form, type: e.target.value })}
      >
        <option>Franchise</option>
        <option>Location</option>
      </select>
      <button onClick={handleSubmit}>Add Store</button>

      <ul>
        {stores.map((s, i) => (
          <li key={i}>
            {s.name} - {s.type}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Stores;
