import React, { useState, useEffect } from "react";

const Stores = () => {
  const [stores, setStores] = useState([]);
  const [form, setForm] = useState({ name: "", type: "Franchise" });
  const [editingIndex, setEditingIndex] = useState(null);

  useEffect(() => {
    fetchStores();
  }, []);

  const fetchStores = async () => {
    const res = await fetch("http://localhost:4000/api/stores");
    const data = await res.json();
    setStores(data);
  };

  const handleSubmit = async () => {
    if (!form.name.trim()) {
      alert("Please enter store name");
      return;
    }

    if (editingIndex !== null) {
      // EDIT mode
      await fetch(`http://localhost:4000/api/stores/${editingIndex}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      alert("Store updated!");
    } else {
      // ADD mode
      await fetch("http://localhost:4000/api/stores", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      alert("Store added!");
    }

    setForm({ name: "", type: "Franchise" });
    setEditingIndex(null);
    fetchStores();
  };

  const handleEdit = (index) => {
    const selected = stores[index];
    setForm({ name: selected.name, type: selected.type });
    setEditingIndex(index);
  };

  const handleDelete = async (index) => {
    if (!window.confirm("Are you sure to delete?")) return;
    await fetch(`http://localhost:4000/api/stores/${index}`, {
      method: "DELETE",
    });
    fetchStores();
  };

  const handleCancel = () => {
    setEditingIndex(null);
    setForm({ name: "", type: "Franchise" });
  };

  const downloadStoresExcel = () => {
    window.open("http://localhost:4000/api/export/stores", "_blank");
  };

  return (
    <div>
      <h2>{editingIndex !== null ? "Edit Store" : "Add Store"}</h2>

      <input
        placeholder="Store Name"
        value={form.name}
        onChange={(e) => setForm({ ...form, name: e.target.value })}
      />
      <select
        value={form.type}
        onChange={(e) => setForm({ ...form, type: e.target.value })}
      >
        <option value="Franchise">Franchise</option>
        <option value="Location">Location</option>
      </select>
      <button onClick={handleSubmit}>
        {editingIndex !== null ? "Update Store" : "Add Store"}
      </button>
      {editingIndex !== null && <button onClick={handleCancel}>Cancel</button>}

      <button onClick={downloadStoresExcel} style={{ marginLeft: "1rem" }}>
        üì• Download Stores Excel
      </button>

      <h3>All Stores</h3>
      <ul>
        {stores.map((s, i) => (
          <li key={i}>
            {s.name} - {s.type}
            <button onClick={() => handleEdit(i)} style={{ marginLeft: "1rem" }}>
              ‚úèÔ∏è Edit
            </button>
            <button onClick={() => handleDelete(i)} style={{ marginLeft: "0.5rem" }}>
              ‚ùå Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Stores;
